import React, { useEffect, useState } from 'react';
import './ScrollableColumn.css'

const ScratcherItem = ({ image, price, name, odds }) => {
  return (
      <div className="scratchers-column">
        <table className="scratchers-table">
          <thead>
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Price</th>
              <th>Odds</th>
            </tr>
          </thead>
          <tbody>
            {scratchers.map(scratcher => (
              <tr key={scratcher.id}>
                <td>
                  <img src={scratcher.image} alt="Scratcher" className="scratcher-image" />
                </td>
                <td>{scratcher.name}</td>
                <td>${scratcher.price}</td>
                <td>{scratcher.odds}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
  );
};


const ScrollableColumn = () => {
  const [scratchers, setScratchers] = useState([]);

  useEffect(() => {
    // Fetch scratcher data from the API
    fetch('http://127.0.0.1:5000/api/scratchers/best-odds?prices=1,2,3,5,10,20,30')
      .then(response => response.json())
      .then(data => setScratchers(data))
      .catch(error => console.log(error));
  }, []);

  return (
    <div className="scratchers-column">
      {scratchers.map(scratcher => (
        <ScratcherItem
          image={scratcher.img_source}
          name={scratcher.name}
          price={scratcher.price}
          odds={scratcher.odds}
        />
      ))}
    </div>
  );
};

export default ScrollableColumn;
